import "./style.css";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Spinner from "./Spinner";

const Home = () => {
  const [crypto, setCrypto] = useState(null);
  const fetchData = async () => {
    const response = await fetch(
      "https://api.coinstats.app/public/v1/coins?skip=0&limit=10&currency=INR"
    );
    setCrypto(await response.json());
  };
  useEffect(() => {
    fetchData();
  }, []);
  return (
    <div className="home">
      {crypto ? (
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Price</th>
              <th>Market Cap</th>
              <th>Volume</th>
              <th>Circulating Supply</th>
            </tr>
          </thead>
          <tbody>
            {crypto.coins.map((currElem, i) => {
              return (
                <tr>
                  <td>{i}</td>
                  <td>
                    <Link
                      to={`coin/${currElem.name.toLowerCase()}`}
                      style={{
                        color: "#ffff",
                        borderBottom: "1px soild yellow",
                      }}
                    >
                      {currElem.name}
                    </Link>
                  </td>
                  <td>â‚¹{currElem.price}</td>
                  <td>{currElem.marketCap}</td>
                  <td>{currElem.volume}</td>
                  <td>{currElem.availableSupply}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      ) : (
        <Spinner />
      )}
    </div>
  );
};

export default Home;
