import './style.css'
import { useState, useEffect } from 'react';
import Table from './Table';

const Home = ()=>{
    const [crypto,setCrypto] = useState([]);
    const fetchData = async ()=>{
        const response = await fetch("https://api.coinstats.app/public/v1/coins?skip=0&limit=10&currency=INR")
        const data = await response.json();
        await setCrypto(data)
    }
    useEffect(()=>{
        fetchData()
    },[])
    return(
        <div className="wrapper">
            {
                Object.keys(crypto).map((currElem,i)=>{
                    return(
                        <Table 
                        key = {currElem.i}
                        id = {currElem.id}
                        name = {currElem.name}
                        price = {currElem.price}
                        cap = {currElem.marketCap}
                        volume = {currElem.volume}
                        supply = {currElem.availableSupply}
                        />
                    )
                })
            }
        </div>
    );
}

export default Home;
