import './style.css'
import { useState, useEffect } from 'react';
import Table from './Table';

const Home = ()=>{
    const [crypto,setCrypto] = useState([]);
    useEffect(()=>{
        document.addEventListener('DOMContentLoaded',async ()=>{
            const response = await fetch("https://api.coinstats.app/public/v1/coins?skip=0&limit=10&currency=INR")
            const data = await response.json();
            setCrypto(data)
            console.log(crypto.coins.name)
        })
    },[ ])
    return(
        <div className="wrapper">
            {
                crypto.coins.map((currElem,i)=>{
                    return(
                        <Table 
                        id = {currElem.i}
                        name = {currElem.name}
                        price = {currElem.price}
                        cap = {currElem.marketCap}
                        volume = {currElem.volume}
                        supply = {currElem.availableSupply}
                        />
                    )
                })
            }
        </div>
    );
}

export default Home;
